import React, { Component } from 'react'
import datetime from 'react-datetime'
import axios from 'axios';

function getAuthorizationTokenUsingMasterKey(verb, resourceType, resourceId, date, masterKey) {  
    var key = new Buffer(masterKey, "base64");  
    var text = (verb || "").toLowerCase() + "\n" +   
               (resourceType || "").toLowerCase() + "\n" +   
               (resourceId || "") + "\n" +   
               date.toLowerCase() + "\n" +   
               "" + "\n";  
    var body = new Buffer(text, "utf8");  
    var signature = crypto.createHmac("sha256", key).update(body).digest("base64");  
    var MasterToken = "master";  
    var TokenVersion = "1.0";  
    return encodeURIComponent("type=" + MasterToken + "&ver=" + TokenVersion + "&sig=" + signature);  
}
class Alerts extends Component {
    state = {
      alerts: []
    }

    componentDidMount() {
        var now = datetime.utcnow().strftime('%a, %d %b %Y %H:%M:00 GMT')
        var key = 'oj2YYNd6SpxZl27NB5NTPgMqaLoFk99ArK9PpEgr1SmILAnyVLLj7keWwOfPNaxGQVwddqRYsBPidirJ8uq0aA=='
        var authStr = getAuthorizationTokenUsingMasterKey(now, key)
        axios.get(`https://besafe.documents.azure.com/dbs/EdgeVision/colls/Alerts/docs`
        // ,{method: "POST",
        // headers: {
        //     'Content-Type': 'application/json',
        //     'Authorization': authStr
        // }}
        ).then(res => {
            const alerts = res.data;
            this.setState({ alerts });
            console.log(res.status);
      })
    //   , function(error) {
    //       error.message
    //   }

    }
    render() {
        return (
        <div className="content">
            <div class="container-fluid">
                    <div class="row">
                        <div class="col-md-12">
                            <div class="card card-plain table-plain-bg">
                              <div class="card-header ">
                                    <h4 class="card-title">Alerts</h4>
                              </div>
                                <div class="card-body table-full-width table-responsive">
                                    <table class="table table-hover">
                                        <thead>
                                            <th>ID</th>
                                            <th>Date</th>
                                            <th>Location</th>
                                            <th>Tag</th>
                                            <th>Image</th>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td>1</td>
                                                <td>05/01/2020</td>
                                                <td>1</td>
                                                <td>4k</td>
                                                <td><img src="https://designpress.com/wp-content/uploads/2017/03/M1.jpg" width = "100" /></td>
                                            </tr>
                                            <tr>
                                                <td>2</td>
                                                <td>05/04/2020</td>
                                                <td>1</td>
                                                <td>hardhat</td>
                                                <td><img src="https://designpress.com/wp-content/uploads/2017/03/M1.jpg" width = "100"/></td>
                                            </tr>

                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
      </div>
    )
  }
}
export default Alerts
