class Alerts extends Component {
    state = {
      alerts: []
    }

    componentDidMount() {
        // var now = datetime.utcnow().strftime('%a, %d %b %Y %H:%M:00 GMT')
        // var key = 'oj2YYNd6SpxZl27NB5NTPgMqaLoFk99ArK9PpEgr1SmILAnyVLLj7keWwOfPNaxGQVwddqRYsBPidirJ8uq0aA=='
        // var authStr = getAuthorizationTokenUsingMasterKey(now, key)
        axios.get(`https://besafe.documents.azure.com/dbs/EdgeVision/colls/Alerts/docs`
